sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var appelantValue = newValue;
	var ga = new GlideAjax('GetUserInfo');
	ga.addParam('sysparm_name','getUserVIP');
	ga.addParam('sysparm_sysId', appelantValue);
	ga.getXMLAnswer(function(answer){
		var data = JSON.parse(answer);
		if(data.vip === 'true'){
			alert(data.vip);
			//var myVar = $('sys_display.' + g_form.getControl('u_beneficiaire').id);
			//myVar.setStyle({color: "red"});
		}

		g_form.setValue('u_beneficiaire', newValue);

	});

}
