sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var service = g_form.getValue('sys_display.x_631216_yunit_yunit.business_service');
	var action = g_form.getValue('u_action');
	var symtom = g_form.getValue('u_symptome');
	var ci = g_form.getValue('sys_display.x_631216_yunit_yunit.u_element_de_configuration');
	var title = '';
   //Type appropriate comment here, and begin script below
	if(action != '' && service != ''){
		title = service + '-' + ci + '-' + action;
	}
	else if(symtom != '' && service != ''){
		title = service + '-' + ci + '-' + symtom;
	}else if(service != ''){
		title = service + '-' + ci;
	}else{
		title = '-' +ci;
	}	

	jslog(title);
	g_form.setValue('u_title', title);   
}
